import './App.css';
import React, { Component } from "react";
import Chart from "react-apexcharts";
import html2canvas from 'html2canvas'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      options: {
        chart: {
          id: "basic-bar"
        },
        xaxis: {
          categories: [1991, 1992, 1993, 1994, 1995, 1996, 1997, 1998]
        }
      },
      series: [
        {
          name: "series-1",
          data: [30, 40, 45, 50, 49, 60, 70, 91]
        }
      ]
    }
  }

  _onCapture = () => {

    const options = {
  scale: 1,
  allowTaint: true, useCORS: true,
  foreignObjectRendering: true

};


        var nodesToRecover = [];
        var nodesToRemove = [];

        var targetElem = document.body;

        var svgElem = targetElem.find('svg');

        svgElem.each(function(index, node) {
        var parentNode = node.parentNode;
        var svg = parentNode.innerHTML;

        var canvas = document.createElement('canvas');

        canvg(canvas, svg);

        nodesToRecover.push({
        parent: parentNode,
        child: node
        });
        parentNode.removeChild(node);

        nodesToRemove.push({
        parent: parentNode,
        child: canvas
        });

        parentNode.appendChild(canvas);
        });

    html2canvas(targetElem, options).then(canvas => {
      //this.clipImage = canvas.toDataURL('image/png');

      document.body.appendChild(canvas);

  });


    /*html2canvas(document.body)
    .then(canvas => this._saveAs(canvas.toDataURL(), 'screenshot'))
    .catch(err => console.log('err', err))*/
  }

	_saveAs = (uri, filename) => {
		let link = document.createElement('a');
		if (typeof link.download === 'string') {
			link.href = uri;
			link.download = filename;
			link.click();
		} else {
			window.open(uri);
		}
  }


  render(){
    return (
      <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={this.state.options}
              series={this.state.series}
              type="bar"
              width="600"
            />
          </div>
        </div>
         <div id="content1">
        <p><i className="fa fa-user fa-lg"></i> fa-camera-retro</p>
       </div>
        <div className='text-center'>


          <i className="fab fa-facebook fa-5x"></i>
          <i className="fab fa-twitter fa-5x"></i>
          <i className="fab fa-youtube fa-5x"></i>
          <i className="fas fa-cloud fa-5x"></i>
        </div>
        <button className="btn btn-info mt-3" onClick={this._onCapture}>Screenshot</button>
      </div>
    )
  }
}

export default App;
