import './App.css';
import React, { Component } from "react";
import Chart from "react-apexcharts";
import html2canvas from 'html2canvas'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      options: {
        chart: {
          id: "basic-bar"
        },
        xaxis: {
          categories: [1991, 1992, 1993, 1994, 1995, 1996, 1997, 1998]
        }
      },
      series: [
        {
          name: "series-1",
          data: [30, 40, 45, 50, 49, 60, 70, 91]
        }
      ]
    }
  }

  _onCapture = () => {
   /* html2canvas(document.body).
    .then(canvas => this._saveAs(canvas.toDataURL(), 'screenshot'))
    .catch(err => console.log('err', err))*/


   /* html2canvas(document.body).then(function(canvas) {

                    document.body.appendChild(canvas);
                });*/


    //html2canvas

    const img = new Image();
img.onload = () => {
  const c = this.$refs.crossImg;
  const ctx = c.getContext('2d');
  ctx.drawImage(img, 0, 0, c.width, c.height);
  this.crossImgBase64 = c.toDataURL('image/png');

  // 2.Draw
  Html2canvas(this.$refs.html, {
    // useCORS: true,
  }).then((canvas) => {
    this.pngBase64 = canvas.toDataURL('image/png');
    this.isShowPng = true;
  });
};
img.crossOrigin = 'anonymous';
img.src = 'https://secure.gravatar.com/avatar/03af4ed2fa8526e9f37c847cc4083141';

     //"logging": true,

     //, { letterRendering: 1, allowTaint : true, onrendered : function (canvas) { } }




    /*

html2canvas(document.querySelector("#capture")).then(canvas => {
    document.body.appendChild(canvas)
});
    */
  }

	_saveAs = (uri, filename) => {
		let link = document.createElement('a');
		if (typeof link.download === 'string') {
			link.href = uri;
			link.download = filename;
			link.click();
		} else {
			window.open(uri);
		}
  }


  render(){
    return (
      <div className="app" id="main">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={this.state.options}
              series={this.state.series}
              type="bar"
              width="600"
            />
          </div>
        </div>
        <div className='text-center'>
          <i className="fab fa-facebook fa-5x"></i>
          <i className="fab fa-twitter fa-5x"></i>
          <i className="fab fa-youtube fa-5x"></i>
          <i className="fas fa-cloud fa-5x"></i>
        </div>
        <button className="btn btn-info mt-3" onClick={this._onCapture}>Screenshot</button>
      </div>
    )
  }
}

export default App;
