import './App.css';
import React, { Component } from "react";
import Chart from "react-apexcharts";
import html2canvas from 'html2canvas'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      options: {
        chart: {
          id: "basic-bar"
        },
        xaxis: {
          categories: [1991, 1992, 1993, 1994, 1995, 1996, 1997, 1998]
        }
      },
      series: [
        {
          name: "series-1",
          data: [30, 40, 45, 50, 49, 60, 70, 91]
        }
      ]
    }
  }

  _onCapture = () => {

    const options = {
  scale: 1,
  foreignObjectRendering: true,
  onclone: (element) => {
    const svgElements: any[] = element.body.getElementsByTagName('svg');
    Array.from(svgElements).forEach((svgElement) => {
        const bBox: any = svgElement.getBBox();
        svgElement.setAttribute('width', bBox.width);
        svgElement.setAttribute('height', bBox.height);
    });
  },
};

    html2canvas(document.getElementById('main'), options).then(canvas => {
      this.clipImage = canvas.toDataURL('image/png');

      document.body.appendChild(this.clipImage);

  });
    /*html2canvas(document.body)
    .then(canvas => this._saveAs(canvas.toDataURL(), 'screenshot'))
    .catch(err => console.log('err', err))*/
  }

	_saveAs = (uri, filename) => {
		let link = document.createElement('a');
		if (typeof link.download === 'string') {
			link.href = uri;
			link.download = filename;
			link.click();
		} else {
			window.open(uri);
		}
  }


  render(){
    return (
      <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={this.state.options}
              series={this.state.series}
              type="bar"
              width="600"
            />
          </div>
        </div>
        <div className='text-center'>
          <i className="fab fa-facebook fa-5x"></i>
          <i className="fab fa-twitter fa-5x"></i>
          <i className="fab fa-youtube fa-5x"></i>
          <i className="fas fa-cloud fa-5x"></i>
        </div>
        <button className="btn btn-info mt-3" onClick={this._onCapture}>Screenshot</button>
      </div>
    )
  }
}

export default App;
